async function loadGameFIlters() {
    await getGameFilters()
    .then((data) => {
        gameFilters = data;
        // console.log("gameFilters loaded: ", gameFilters);
    })
    .catch((error) => {
        console.error("Error loading booleans:", error);
    });
    // console.log("gameFilters loaded: ", gameFilters);

    createVerticalColumn("container-main", main, sort = true);
    createVerticalColumn("container-cross", cross, sort = false);
    createVerticalColumn("container-niches", niches, sort = true);
}

