function createVerticalColumn(containerId, _items, sort) {
    let container = document.getElementById(containerId);
    let items = [];

    // console.log("create ["+containerId +"] with ["+_items.length+"] items : ", _items);

    if (sort)
        _items.sort((a, b) => a.name.localeCompare(b.name));

    // Create the "All" item
    let allItem = document.createElement("div");
    allItem.id ='item-all';
    allItem.className = "item all";
    allItem.innerHTML = `
        <span class="svg-theme"><svg xmlns="http://www.w3.org/2000/svg" height="21px" viewBox="0 -960 960 960" width="24px" fill="undefined"><path d="M440-760v-160h80v160h-80Zm266 110-55-55 112-115 56 57-113 113Zm54 210v-80h160v80H760ZM440-40v-160h80v160h-80ZM254-652 140-763l57-56 113 113-56 54Zm508 512L651-255l54-54 114 110-57 59ZM40-440v-80h160v80H40Zm157 300-56-57 112-112 29 27 29 28-114 114Zm283-100q-100 0-170-70t-70-170q0-100 70-170t170-70q100 0 170 70t70 170q0 100-70 170t-170 70Zm0-80q66 0 113-47t47-113q0-66-47-113t-113-47q-66 0-113 47t-47 113q0 66 47 113t113 47Zm0-160Z"/></svg></span>
        <span class="text">ALL</span>
        <span class="svg-icon">
            <svg xmlns="http://www.w3.org/2000/svg" height="20px" viewBox="0 -960 960 960" width="20px" fill="#03a9f4">
                <path d="M379.33-244 154-469.33 201.67-517l177.66 177.67 378.34-378.34L805.33-670l-426 426Z"/>
            </svg>
        </span>
    `;
    allItem.addEventListener("click", () => {
        const isSelected = allItem.classList.toggle("selected");
        items.forEach(item => {
            if (isSelected) {
                item.classList.add("selected");
                gameFilters[item.id] = true;
            } else {
                item.classList.remove("selected");
                gameFilters[item.id] = false;
            }
        });
    });
    container.appendChild(allItem);

    // Create the other items
    for (let i = 0; i < _items.length; i++) {

        if (_items[i].info === true) {
            const item = document.createElement("div");
            // items.push(item);
            item.className = "info"; // TODO IS INFO NOT ITEM
            item.innerHTML = `
                <span class="text">${_items[i].name}</span>
            `;
            container.appendChild(item);
            continue;
        }
        const item = document.createElement("div");
        item.id = _items[i].nameDB;
        item.className = "item";
        item.innerHTML = `
            <span class="svg-theme">${_items[i].svg}</span>
            <span class="text">${_items[i].name}</span>
            <span class="svg-icon">
                <svg xmlns="http://www.w3.org/2000/svg" height="18px" viewBox="0 -960 960 960" width="20px" fill="#03a9f4">
                    <path d="M379.33-244 154-469.33 201.67-517l177.66 177.67 378.34-378.34L805.33-670l-426 426Z"/>
                </svg>
            </span>
        `;

        if (_items[i].lock === true) {
            item.className = "lock";
            item.innerHTML = `
            <span class="svg-theme">${_items[i].svg}</span>
            <span class="text">${_items[i].name}</span>
            <span class="svg-icon">
                <svg xmlns="http://www.w3.org/2000/svg" height="16px" viewBox="0 -960 960 960" width="40px" fill="#e8eaed"><path d="M226.67-80q-27.5 0-47.09-19.58Q160-119.17 160-146.67v-422.66q0-27.5 19.58-47.09Q199.17-636 226.67-636h60v-90.67q0-80.23 56.57-136.78T480.07-920q80.26 0 136.76 56.55 56.5 56.55 56.5 136.78V-636h60q27.5 0 47.09 19.58Q800-596.83 800-569.33v422.66q0 27.5-19.58 47.09Q760.83-80 733.33-80H226.67Zm0-66.67h506.66v-422.66H226.67v422.66ZM480.11-280q32.22 0 55.06-22.52Q558-325.04 558-356.67q0-31-22.95-55.16Q512.11-436 479.89-436t-55.06 24.17Q402-387.67 402-356.33q0 31.33 22.95 53.83 22.94 22.5 55.16 22.5ZM353.33-636h253.34v-90.67q0-52.77-36.92-89.72-36.93-36.94-89.67-36.94-52.75 0-89.75 36.94-37 36.95-37 89.72V-636ZM226.67-146.67v-422.66 422.66Z"/></svg>
            </span>
            `;
            container.appendChild(item);
            if (gameFilters[_items[i].nameDB] === true)
            {
                gameFilters[_items[i].nameDB] = false;
                console.warn('set ['+_items[i].nameDB+'] = false ; is lock');

            }
            continue;
        }

        // Add click event for selection
        item.addEventListener("click", () => {
            const isSelected = item.classList.toggle("selected");
            if (isSelected) {
                gameFilters[item.id] = true;
            }
            else {
                gameFilters[item.id] = false;
            }

            // Check if all items are selected or not
            const allSelected = items.every(it => it.classList.contains("selected"));
            if (allSelected) {
                allItem.classList.add("selected");
            } else {
                allItem.classList.remove("selected");
            }
        });

        items.push(item);
        container.appendChild(item);
        if (gameFilters[_items[i].nameDB] === true)
        {
            item.click();
            // console.log('selected ['+_items[i].nameDB+']');
        }
    }
    const allSelected = items.every(it => it.classList.contains("selected"));
    if (allSelected) {
        allItem.classList.add("selected");
    } else {
        allItem.classList.remove("selected");
    }
    // console.log(gameFilters);
}

